function firebaseCreateAccount(){const e=document.querySelector("#email-createAccount"),t=document.querySelector("#password-createAccount"),n=document.querySelector("#submit-createAccount"),a=document.querySelector("#feedback-createAccount");var c,u;e.addEventListener("input",function(){c=this.value}),t.addEventListener("input",function(){u=this.value}),n.addEventListener("click",function(e){e.preventDefault();var t=c,n=u;firebase.auth().createUserWithEmailAndPassword(t,n).catch(function(e){e.code,e.message;a.innerHTML="<strong>"+e.code+"</strong><br />"+e.message})})}function firebaseAuthStateChanged(){const e=document.querySelector("#feedback-authedAccount");firebase.auth().onAuthStateChanged(function(t){t?e.innerHTML=t.email+" is authed":e.innerHTML="No authed account"})}function firebaseAuth(){const e=document.querySelector("#email-authAccount"),t=document.querySelector("#password-authAccount"),n=document.querySelector("#submit-authAccount"),a=document.querySelector("#feedback-authAccount");var c,u;e.addEventListener("input",function(){c=this.value}),t.addEventListener("input",function(){u=this.value}),n.addEventListener("click",function(e){e.preventDefault();var t=c,n=u;firebase.auth().signInWithEmailAndPassword(t,n).catch(function(e){e.code,e.message;a.innerHTML="<strong>"+e.code+"</strong><br />"+e.message})})}function firebaseDatabase(){firebase.database()}firebaseCreateAccount(),firebaseAuth(),firebaseAuthStateChanged(),firebaseDatabase();