function firebaseCreateAccount(){const e=document.querySelector("#email-createAccount"),t=document.querySelector("#password-createAccount"),n=document.querySelector("#submit-createAccount"),c=document.querySelector("#feedback-createAccount");var a,u;e.addEventListener("input",function(){a=this.value}),t.addEventListener("input",function(){u=this.value}),n.addEventListener("click",function(e){e.preventDefault();var t=a,n=u;firebase.auth().createUserWithEmailAndPassword(t,n).catch(function(e){e.code,e.message;c.innerHTML="<strong>"+e.code+"</strong><br />"+e.message})})}function firebaseAuthStateChanged(){const e=document.querySelector("#feedback-authedAccount");firebase.auth().onAuthStateChanged(function(t){t?e.innerHTML=t.email+" is authed":e.innerHTML="No authed account"})}function firebaseAuth(){const e=document.querySelector("#email-authAccount"),t=document.querySelector("#password-authAccount"),n=document.querySelector("#submit-authAccount"),c=document.querySelector("#feedback-authAccount");var a,u;e.addEventListener("input",function(){a=this.value}),t.addEventListener("input",function(){u=this.value}),n.addEventListener("click",function(e){e.preventDefault();var t=a,n=u;firebase.auth().signInWithEmailAndPassword(t,n).catch(function(e){e.code,e.message;c.innerHTML="<strong>"+e.code+"</strong><br />"+e.message})})}function firebaseSignOut(){const e=document.querySelector("#auth-signOut");e.addEventListener("click",function(){console.clear(),console.log("üçª"),firebase.auth().signOut().then(function(){},function(e){})})}function firebaseDatabase(){firebase.database()}firebaseCreateAccount(),firebaseAuth(),firebaseAuthStateChanged(),firebaseSignOut(),firebaseDatabase();